package src.main.java.oneWeekPrepKit.day4

import java.math.BigInteger


/*
 * Complete the 'superDigit' function below.
 *
 * The function is expected to return an INTEGER.
 * The function accepts following parameters:
 *  1. STRING n
 *  2. INTEGER k
 */

fun superDigit(n: String, k: Int): Int {
    // Write your code here
//    val s = if (k > 0) n.repeat(k) else n
    val s: String = if (k == 0) n else n.repeat(k)
    var sum: BigInteger = BigInteger.ZERO
    for (i in s.indices) {
        sum += s[i].toString().toBigInteger()
    }
    if (sum.toString().length > 1) {
        return superDigit(sum.toString(), 0)
    }

//    print("$sum // ")
    /*    while (sum.toString().length > 1) {
            val s = sum.toString()
            sum = 0
            for (i in s.indices) {
                sum += s[i].toString().toInt()
            }
        }*/

    return sum.toInt()
}

fun superDigitEditorial(n: String, k: Int): Int {
    // Base case: if n has only one digit, return it
    if (n.length == 1) {
        return n.toInt()
    }

    // Calculate the sum of the digits in n
    var sum = 0
    for (i in n.indices) {
        sum += n[i] - '0'
    }

    // Calculate the super-digit of the sum multiplied by k
    val superDigitOfSum = superDigitEditorial((sum * k).toString(), 1)

    return superDigitOfSum
}


fun main(args: Array<String>) {
//    val n = ""
//    val k = 100000
    val n = "9875"
    val k = 4
    val result = superDigitEditorial(n, k)

    println(result)
}

